(() => {
  "use strict";
  const e = document?.querySelector("[data-burger]"),
    t = document?.querySelector("[data-nav]"),
    o = t?.querySelectorAll("a"),
    c = document.body,
    n = document?.querySelector(".header"),
    s = n.offsetHeight;
  console.log(s),
    document
      .querySelector(":root")
      .style.setProperty("--header-height", `${s}px`),
    e?.addEventListener("click", () => {
      c.classList.toggle("stop-scroll"),
        e?.classList.toggle("burger--active"),
        t?.classList.toggle("nav--visible");
    }),
    o.forEach((o) => {
      o.addEventListener("click", () => {
        c.classList.remove("stop-scroll"),
          e?.classList.remove("burger--active"),
          t?.classList.remove("nav--visible");
      });
    });
  const a = document?.querySelector("[data-button-add]"),
    l = document?.querySelector("[data-content-add]");
  var r, i;
  document?.querySelector("[data-button-change]"),
    a?.addEventListener("click", () => {
      l.classList.toggle("content--active"),
        a.classList.toggle("button__add--active"),
        (a.innerHTML =
          "Закрыть панель" === a.innerHTML
            ? (a.innerHTML = "Окрыть панель")
            : "Закрыть панель");
    }),
    (r = function (e) {
      let t = !0 === e ? "webp" : "no-webp";
      document.documentElement.classList.add(t);
    }),
    ((i = new Image()).onload = i.onerror =
      function () {
        r(2 == i.height);
      }),
    (i.src =
      "data:image/webp;base64,UklGRjoAAABXRUJQVlA4IC4AAACyAgCdASoCAAIALmk0mk0iIiIiIgBoSygABc6WWgAA/veff/0PP8bA//LwYAAA"),
    $(".input-file input[type=file]").on("change", function () {
      let e = this.files[0];
      $(this).next().html(e.name);
    });
})();
